Modules.addCached("Font4x5",function(){exports.add=function(a){a.prototype.setFont4x5=function(){this.setFontCustom(atob(""),32,4,5)}}});

const offset = 25;
const width = g.getWidth();
const height = g.getHeight();
const font = "Vector:12";

const locale = require("locale");

var img = {
  width : 176, height : 149, bpp : 4,
  transparent : -1,
  palette : new Uint16Array([25804,806,0,21514]),
  buffer : require("heatshrink").decompress((atob("")))
};

var night= {
  width : 89, height : 76, bpp : 4,
  transparent : 2,
  buffer : (atob(""))
};

var time= "10:20";

function time() { //numbers
  // work out how to display the current time
  const d = new Date();
  const h = d.getHours(),
    m = d.getMinutes();
  const time = h + ":" + ("0" + m).substr(-2);
  const day = Date.now();
  const mo = d.getMonth()+1;
  const damo = d.getDate();
  
  var dayMonth = mo+"-"+damo;
  
  // time
  require("Font4x5").add(Graphics);
  isDark();
  g.setFontAlign(0,0);
  //g.setFont("6x8:4x5");
  g.setFont("4x5",7);
  g.drawString(time, width/2, height/2);  
  // date  
  require("Font4x5").add(Graphics);
  g.setFontAlign(1,1);
  //g.setFont("4x6",2);
  g.setFont("4x5",3);
  g.drawString(dayMonth, width/2+60, height/2+40);
  
}

function isDark(){
   if (g.theme.dark==true){
    g.setColor(0xFFFF);
  }
  else {
    g.setColor(0x0000);
  }
}

function draw() { //poketch background  
  if (g.theme.dark==true){
    g.drawImage(night, 0, 25, {scale:2}); //poketch is life  
  }
  else {
    g.drawImage(img, 0, 25); //poketch is life  
  }  
  time();
}

//program start
g.clear();
draw();
var secondInterval = setInterval(draw, 1000); // Stop updates when LCD is off, restart when on
Bangle.on('lcdPower',on=>{
  if (secondInterval) clearInterval(secondInterval);
  secondInterval = undefined;
  if (on) {
    secondInterval = setInterval(draw, 1000);
    draw(); // draw immediately
  }
});
// Show launcher when middle button pressed
Bangle.setUI("clock");
// Load widgets
Bangle.loadWidgets();
Bangle.drawWidgets();
